package main

import "fmt"

func main() {
	//fmt.Println(findSubstringInWraproundString("a"))
	//fmt.Println(findSubstringInWraproundString("cac"))
	fmt.Println(findSubstringInWraproundString("zab"))
	fmt.Println(findSubstringInWraproundString(""))
}

func findSubstringInWraproundString(p string) int {
	var dp [26]int
	var k = 0
	for i := 0; i < len(p); i++ {
		if i > 0 && (p[i]-p[i-1]+26)%26 == 1 {
			k++
		} else {
			k = 1
		}

		dp[p[i]-'a'] = max(k, dp[p[i]-'a'])
	}

	var r int
	for _, v := range dp {
		r += v
	}

	return r
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}
