const mod int = 1e9 + 7

func threeSumMulti(arr []int, target int) int {
	mp := make(map[int]int)
	for i := range arr {
		mp[arr[i]]++
	}

	list := make([][2]int, 0)
	for k, v := range mp {
		list = append(list, [2]int{k, v})
	}

	sort.Slice(list, func(i, j int) bool {
		return list[i][0] < list[j][0]
	})

	var dfs func(int, int, int) int
	dfs = func(index, count, value int) (r int) {
		if count == 0 {
			if value == 0 {
				return 1
			}
			return 0
		}

		for i := index; i < len(list); i++ {
			msg := list[i]
			if msg[0] > value {
				break
			}

			r = (r + (msg[1]*dfs(i+1, count-1, value-msg[0]))%mod) % mod

			if count >= 2 && msg[1] >= 2 && value-2*msg[0] >= 0 {
				cc := msg[1] * (msg[1] - 1) / 2
				r = (r + (cc*dfs(i+1, count-2, value-2*msg[0]))%mod) % mod
			}

			if count >= 3 && msg[1] >= 3 && value-3*msg[0] >= 0 {
				cc := msg[1] * (msg[1] - 1) * (msg[1] - 2) / 6
				r = (r + (cc*dfs(i+1, count-3, value-3*msg[0]))%mod) % mod
			}
		}

		return r
	}

	r := dfs(0, 3, target)
	return r
}
